sp_widget.script:
(function(){
	var gr = $sp.getInstanceRecord();
	data.href = $sp.getMenuHREF(gr);
	data.href = "";
	data.target = options.target || "";
	data.target = "";
	//bell-slash

	var story = "";

	if (input) {
		//gs.addInfoMessage("STATE = " + input.story_state);
			story = new GlideRecord('x_145082_stories_story');
			story.addQuery('state',2).addOrCondition('state',-5);
			story.setLimit(1);
			story.query();

			if (story.next()) {
				if (story.state == 2) story.state = -5;
				else if (story.state == -5) story.state = 2;
				data.story_state = story.state.toString();
				//gs.addInfoMessage("STATE = " + data.story_state);
				story.update();				
			}		

			else {

			}
	}

			story = new GlideRecord('x_145082_stories_story');
			story.addQuery('state',2).addOrCondition('state',-5);
			story.setLimit(1);
			story.query();

			if (story.next()) {
				data.story_number = story.number.toString();
				data.story_short_description = story.short_description.toString();
				data.story_description = story.description.toString();
				data.story_state = story.state.toString();
			} else {
				data.story_number = "";
				data.story_short_description = "";
				data.story_description = "";
				data.story_state = "";
			}



	//console.log('INPUT',input);
	/*
		if (input) {		
			story = new GlideRecord('x_145082_stories_story');
