		var day = parseInt(x.getDayOfWeekLocalTime());

		if (day == 1) day = -7
		else day = -(day-1);

		data.day_list = [];

		x.addDaysLocalTime(day);
		var monday = x.getValue();

		x.addDaysLocalTime(+1);
		var tuesday = x.getValue();
		x.addDaysLocalTime(+1);
		var wednesday = x.getValue();
		x.addDaysLocalTime(+1);
		var thursday = x.getValue();
		x.addDaysLocalTime(+1);
		var friday = x.getValue();

