		data.day_list.push('TUESDAY');
		var card = new GlideRecord('task_time_worked');
		card.addQuery('sys_updated_on','>',tuesday);
		card.addQuery('sys_updated_on','<',wednesday);
		card.orderBy('task.short_description');
		card.query();
		while (card.next()){ data.day_list.push("  - " + card.task.short_description + " " +card.task.description + " : " +card.comments + " [" + card.time_worked.slice(11) + "]"); }

		data.day_list.push('WEDNESDAY');
		var card = new GlideRecord('task_time_worked');
		card.addQuery('sys_updated_on','>',wednesday);
		card.addQuery('sys_updated_on','<',thursday);
		card.orderBy('task.short_description');
		card.query();
		while (card.next()){ data.day_list.push("  - " + card.task.short_description + " " +card.task.description + " : " +card.comments + " [" + card.time_worked.slice(11) + "]"); }

		data.day_list.push('THURSDAY');
		var card = new GlideRecord('task_time_worked');
		card.addQuery('sys_updated_on','>',thursday);
		card.addQuery('sys_updated_on','<',friday);
		card.orderBy('task.short_description');
		card.query();
		while (card.next()){ data.day_list.push("  - " + card.task.short_description + " " +card.task.description + " : " +card.comments + " [" + card.time_worked.slice(11) + "]"); }

		data.day_list.push('FRIDAY');
		var card = new GlideRecord('task_time_worked');
		card.addQuery('sys_updated_on','>',friday);
		card.orderBy('task.short_description');
		card.query();
		while (card.next()){ 
			data.day_list.push("  - " + card.task.short_description + " " +card.task.description + " : " +card.comments + " [" + card.time_worked.slice(11) + "]"); 
		}




	function display_counter(x) {
		if (x < 60) return '' + x + 's';
		var min = Math.floor(x/60);
		var sec = x - (min *60);
		if (x < 3600) return '' + min  + 'min '+ sec + 's';
		var h = Math.floor(x/3600);
		min = Math.floor((x - (h*3600))/60);
		sec = x - (h * 3600) - (min * 60);
		return '' + h + 'h ' + min  + 'min '+ sec + 's';

	}

	if (input){
		//gs.addInfoMessage("ANSWER" + input.importStories);
		if (input.importStories){


			var user = input.user;
			var password = input.user_pwd;

			var requestUser = new sn_ws.RESTMessageV2();
			requestUser.setBasicAuth(user,password);
			requestUser.setRequestHeader("Accept","application/json");
			requestUser.setEndpoint('https://support.spoc.pl/api/now/table/sys_user?sysparm_query=email%3D'+
													user+
													'&sysparm_fields=sys_id');

			requestUser.setHttpMethod('GET');

			var responseUser = requestUser.execute();
			//var objUserX = JSON.parse(responseUser[0]);
			//gs.addInfoMessage(responseUser.getStatusCode());
			//gs.log(response.getBody());
			//gs.addInfoMessage(response.getBody());
			//gs.info(response.getBody());

			//gs.addInfoMessage("ANSWER" + data.import_answer);
			//for (var i=0;i<objUser.result.length;i++) gs.info(objUser.result[i].sys_id );

			if (responseUser.getStatusCode() != 200) data.import_answer = "ERROR" + responseUser.getStatusCode();
			else {
				  var objUser = JSON.parse(responseUser.getBody());
		      data.foundUser = objUser.result.length.toString();
					var foundUser = objUser.result[0].sys_id.toString();

					var request = new sn_ws.RESTMessageV2();

					//request.setEndpoint('https://support.spoc.pl/api/now/table/x_mwtso_ppm_story?sysparm_query=assigned_to%3Dc2e35d38377bf200811dfa7b34990ef4%255Estate!%253D303%255EORstate%253DNULL%255Estate!%253D307%255EORstate%253DNULL%255Estate!%253D304%255EORstate%253DNULL%255Estate!%253D305%255EORstate%253DNULL%255Eblocked%253Dfalse&sysparm_fields=number%2Cshort_description');
					request.setEndpoint('https://support.spoc.pl/api/now/table/x_mwtso_ppm_story?sysparm_query=assigned_to%3D'+
															//'c2e35d38377bf200811dfa7b34990ef4'+
															foundUser +
															'%5Estate!%3D303%5Estate!%3D307%5Estate!%3D304%5Estate!%3D305%5Eblocked!%3Dtrue&sysparm_fields=number%2Cshort_description%2Cstate%2Cblocked');

					request.setHttpMethod('GET');
					request.setBasicAuth(user,password);
					request.setRequestHeader("Accept","application/json");

					var response = request.execute();
					//gs.addInfoMessage('EXEC');
					//gs.log(response.getBody());
					//gs.addInfoMessage(response.getBody());
					//gs.info(response.getBody());
				  if (response.getStatusCode() != 200) data.import_answer = "ERROR" + responseUser.getStatusCode();
				  else {
							var obj = JSON.parse(response.getBody());
							data.import_answer = obj.result.length.toString();
							//gs.addInfoMessage("ANSWER" + data.import_answer);
							for (var i=0;i<obj.result.length;i++) gs.info(obj.result[i].number + " " + obj.result[i].short_description);
					}
				}
		 }
		}
})();
